            dispatch({
                type: LOAD_FOLLOWERS_REQUEST,
                data: {
                    limit: 3
                }
            });
            dispatch({
                type: LOAD_FOLLOWINGS_REQUEST,
                data: {
                    limit: 3
                }
            });
        }
    }, []);

    return (
        <>
            <Head>
                <title>내 프로필 | NodeBird</title>
            </Head>

            <AppLayout>
                <NicknameEditForm />
                <FollowList header="팔로잉" data={me.Followings} />
                <FollowList header="팔로워" data={me.Followers} />
            </AppLayout>
        </>
    );
};


