import React, { useCallback } from 'react'
import useInput from '../hooks/useInput';
import { TweetForm, TweetButton, TweetTextArea } from "./style/PostFormStyle.js";

// import { addPost } from '../reducers/post';
import { ADD_POST_REQUEST } from '../reducers/post';
import { useDispatch, useSelector } from 'react-redux';


function PostForm() {
    const dispatch = useDispatch();
    const [text, onChangeText, setText] = useInput("");
    const { addPostLoading, addPostDone } = useSelector((state) => state.post);

    useEffect(() => {
        if (addPostDone) {
            setText('');
        }
    }, [addPostDone]);

    const onSubmit = useCallback((e) => {
        e.preventDefault();
        console.log("입력 text : " + text);

        // dispatch(addPost);
        // setText("");
        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                text,
            },
        });

    }, [text]);

    return (
        <TweetForm onSubmit={onSubmit}>
            <TweetTextArea
                rows={4}
                value={text}
                onChange={onChangeText}
                maxLength={140}
                placeholder="어떤 신기한 일이 있었나요?"
            />
            <input type="file" multiple hidden />
            <TweetButton primary >이미지 업로드</TweetButton>
            <TweetButton type="submit" primary right loading={addPostLoading}>Tweet</TweetButton>
        </TweetForm>
    )
}


export default PostForm
