import React, { useEffect, useCallback } from "react";
import AppLayout from "../../../components/AppLayout";
import Head from "next/head";
import PostForm from "../../../components/PostForm";
import PostCard from "../../../components/PostCard";
import { SEARCH_POSTS_REQUEST } from '../../../reducers/post';
import { LOAD_USER_REQUEST } from '../../../reducers/user';
import { useSelector, useDispatch } from 'react-redux';
import wrapper from '../../../store/configureStore';
import { END } from 'redux-saga';
import axios from 'axios';
import { Button } from "antd";
import { useRouter } from 'next/router';


const SearchPost = () => {
    const { me } = useSelector((state) => state.user);
    const { mainPosts, searchPostsDone } = useSelector((state) => state.post);
    const router = useRouter();
    const dispatch = useDispatch();
    const { search_word } = router.query;
    console.log("search_word : "+ search_word);
    console.log("search_word : " + searchPostsDone);


    useEffect(()=> {
        dispatch({
            type: SEARCH_POSTS_REQUEST,
            data: search_word
        });
    }, [search_word])



    return (
        <AppLayout>
            <Head>
                <title>Home</title>
            </Head>
            {/* {me && <PostForm />} */}
            search page {search_word} <br/>
            hi
            {mainPosts.map((c) => {
                return (
                    <PostCard key={c.id} post={c} />
                );
            })}
        </AppLayout>
    );
};

