import AppLayout from "../components/AppLayout";
import Head from "next/head";
import NicknameEditForm from '../components/NicknameEditForm';
import FollowList from '../components/FollowList';
import Router from 'next/router';
import { useSelector, useDispatch, } from 'react-redux';
import { LOAD_FOLLOWERS_REQUEST, LOAD_FOLLOWINGS_REQUEST, LOAD_USER_REQUEST } from '../reducers/user';

// useState, useCallback
import React, { useEffect, useState, useCallback } from "react";
// // fetcher 추가
// const fetcher = (url) => axios.get(url, { withCredentials: true }).then((result) => result.data);
// // swr 모듈 임포트 
// import useSWR from 'swr';
// wrapper 임포트
import wrapper from '../store/configureStore';
// axios
import { END } from 'redux-saga';
import axios from 'axios';

const Profile = () => {
    const dispatch = useDispatch();
    const { me, loadFollowingsDone } = useSelector((state) => state.user);

    const [followingsLimit, setFollowingsLimit] = useState(3);
    const [followersLimit, setFollowersLimit] = useState(3);

    // if (!me) {
    //     return null;
    // }

    const loadMoreFollowers = useCallback(() => {
        setFollowersLimit((prev) => prev + 3);
    }, []);

    const loadMoreFollowings = useCallback(() => {
        setFollowingsLimit((prev) => prev + 3);
    }, []);

    useEffect(() => {
        if (!(me && me.id)) {
            Router.push('/');
        }
    }, [me && me.id]);

    useEffect(() => {

        dispatch({
            type: LOAD_USER_REQUEST,
        });

        if (me && me.id) {
            dispatch({
                type: LOAD_FOLLOWERS_REQUEST,
                data: {
                    limit: followingsLimit
                }
            });
            dispatch({
                type: LOAD_FOLLOWINGS_REQUEST,
                data: {
                    limit: followersLimit
                }
            });
        }
    }, []);

    return (
        <>
            <Head>
                <title>내 프로필 | NodeBird</title>
            </Head>

            <AppLayout>
                <NicknameEditForm />
                <FollowList header="팔로잉" data={me.Followings} onClickMore={loadMoreFollowings} />
                <FollowList header="팔로워" data={me.Followers} onClickMore={loadMoreFollowings} />
            </AppLayout>
        </>
    );
};


