import React, { useCallback, useEffect } from 'react';
import { Input } from "antd";
import useInput from '../hooks/useInput';
import { useDispatch, useSelector } from 'react-redux';
import { SEARCH_POSTS_REQUEST } from '../reducers/post';
import Router from 'next/router';


function SearchForm() {
    const dispatch = useDispatch();
    const [searchWord, onChangeSearchWord] = useInput('');
    const { me } = useSelector((state) => state.user);
    const { searchPostsLoading, searchPostsDone } = useSelector((state) => state.post);

    const onSearch = useCallback(() => {
        console.log("검색 버튼 클릭 ");
        // alert("검색어 : " + searchWord);

        return dispatch({
            type: SEARCH_POSTS_REQUEST,
            data: searchWord
        });
    }, [searchWord]);

    useEffect(() => {
        if (me && me.id) {
            Router.replace('/');
        }
    }, [me && me.id]);

    return (
        <div>
            <Input.Search
                enterButton style={{ verticalAlign: 'middle' }}
                onChange={onChangeSearchWord}
                onSearch={onSearch}
                loading={searchPostsLoading}
            />
        </div>
    )
}

export default SearchForm
