import { SafetyCertificateTwoTone } from '@ant-design/icons';
import produce from 'immer';

export const initialState = {
    me: null,
    logInLoading: false, // 로그인 시도중
    logInDone: false,
    logInError: null,

    logOutLoading: false, // 로그아웃 시도중
    logOutDone: false,
    logOutError: null,

    followLoading: false, // 팔로우 시도중
    followDone: false,
    followError: null,

    unfollowLoading: false, // 언팔로우 시도중
    unfollowDone: false,
    unfollowError: null,

};

// initialState.mainPosts = initialState.mainPosts.concat(
//     Array(20).fill().map(() => ({
//         id: shortId.generate(),
//         User: {
//             id: shortId.generate(),
//             nickname: faker.name.findName(),
//         },
//         content: faker.lorem.paragraph(),
//         Images: [{
//             src: faker.image.image(),
//         }],
//         Comments: [{
//             User: {
//                 id: shortId.generate(),
//                 nickname: faker.name.findName(),
//             },
//             content: faker.lorem.sentence(),
//         }],
//     })),
// )

// 게시글 생성(using faker) 함수 
export const generateDummyPost = (number) => Array(number).fill().map(() => ({
    id: shortId.generate(),
    User: {
        id: shortId.generate(),
        nickname: faker.name.findName(),
    },
    content: faker.lorem.paragraph(),
    Images: [{
        src: faker.image.image(),
    }],
    Commeents: [{
        User: {
            id: shortId.generate(),
            nickname: faker.name.findName(),
        },
        content: faker.lorem.sentence(),
    }],
}));

// 최초 게시글 10개 설정
initialState.mainPosts = initialState.mainPosts.concat(
    generateDummyPost(10)
)



export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';
export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';
export const ADD_POST_TO_ME = 'ADD_POST_TO_ME';
export const REMOVE_POST_OF_ME = 'REMOVE_POST_OF_ME';

export const FOLLOW_REQUEST = 'FOLLOW_REQUEST';
export const FOLLOW_SUCCESS = 'FOLLOW_SUCCESS';
export const FOLLOW_FAILURE = 'FOLLOW_FAILURE';

export const UNFOLLOW_REQUEST = 'UNFOLLOW_REQUEST';
export const UNFOLLOW_SUCCESS = 'UNFOLLOW_SUCCESS';
export const UNFOLLOW_FAILURE = 'UNFOLLOW_FAILURE';

const dummyUser = (data) => ({
    ...data, // saga에서 넘어온 데이터
    nickname: '제로초',
    id: 1,
    Posts: [{ id: 1 }],
    Followings: [{ id: 1, nickname: '제로초' }, { id: 2, nickname: '케인' }, { id: 3, nickname: '네이마르' }],
    Followers: [{ id: 4, nickname: 'zerocho' }, { id: 5, nickname: 'linux' }, { id: 6, nickname: '이재용' }],
});


export default (state = initialState, action) => {
    return produce(state, (draft) => {
        switch (action.type) {
            case LOG_IN_REQUEST:
                // return {
                //     ...state,
                //     logInLoading: true,
                //     logInError: null,
                //     logInDone: false
                // };
                draft.logInLoading = true;
                draft.loginDone = false;
                draft.lognError = null;
                break;
            case LOG_IN_SUCCESS:
                // return {
                //     ...state,
                //     logInLoading: false,
                //     logInDone: true,
                //     me: dummyUser(action.data),
                // };
                draft.logInLoading = false;
                draft.logindone = true;
                draft.me = dummyUser(action.data);
                break;
            case LOG_IN_FAILURE:
                // return {
                //     ...state,
                //     logInLoading: false,
                //     logInError: action.data
                // };
                draft.logInLoading = false;
                draft.loginDone = false;
                draft.loginError = null;
                break;
            case LOG_OUT_REQUEST:
                // return {
                //     ...state,
                //     logOutLoading: true,
                //     logouterror: null,
                //     logOutDone: false
                // };
                draft.logOutLoading = true;
                draft.logOutDone = false;
                draft.logOutError = null;
                break;
            case LOG_OUT_SUCCESS:
                // return {
                //     ...state,
                //     logOutLoading: false,
                //     logOutDone: true,
                //     me: null,
                // };
                draft.logOutLoading = false;
                draft.logOutDone = true;
                draft.me = null;
                break;
            case LOG_OUT_FAILURE:
                // return {
                //     ...state,
                //     logOutLoading: false,
                //     logouterror: action.error,
                // };
                draft.logOutDone = false;
                draft.logOutLoading = false;
                draft.logOutError = action.error;
                break;
            case ADD_POST_TO_ME:
                // return {
                //     ...state,
                //     me: {
                //         ...state.me,
                //         Posts: [{ id: action.data }, ...state.me.Posts],
                //     },
                // };
                draft.me.Posts.unshift({ id: action.data, ...state.me.Posts })
                break;
            case REMOVE_POST_OF_ME:
                // return {
                //     ...state,
                //     me: {
                //         ...state.me,
                //         Posts: state.me.Posts.filter((v) => v.id !== action.data),
                //     },
                // };
                draft.me.Posts.filter((v) => v.id !== action.data);
                break;

            case FOLLOW_REQUEST:
                draft.followLoading = true;
                draft.followError = null;
                draft.followDone = false;
                break;
            case FOLLOW_SUCCESS:
                draft.followLoading = false;
                draft.me.Followings.push({ id: action.data });
                draft.followDone = true;
                break;
            case FOLLOW_FAILURE:
                draft.followLoading = false;
                draft.followError = action.error;
                break;

            case UNFOLLOW_REQUEST:
                draft.unfollowLoading = true;
                draft.unfollowError = null;
                draft.unfollowDone = false;
                break;
            case UNFOLLOW_SUCCESS:
                draft.unfollowLoading = false;
                draft.me.Followings = draft.me.Followings.filter((v) => v.id !== action.data);
                draft.unfollowDone = true;
                break;
            case UNFOLLOW_FAILURE:
                draft.unfollowLoading = false;
                draft.unfollowError = action.error;
                break;

            default:
                // return state;
                break;
        }
    });
}

// export default reducer;
