import React, { useCallback, useEffect, useRef } from 'react'
import useInput from '../hooks/useInput';
import { TweetForm, TweetButton, TweetTextArea } from "./style/PostFormStyle.js";
import { useDispatch, useSelector } from 'react-redux';
import { Button } from "antd";
import { ADD_POST_REQUEST, UPLOAD_IMAGES_REQUEST } from '../reducers/post';


function PostForm() {
    const dispatch = useDispatch();
    const [text, onChangeText, setText] = useInput("");
    const imageInput = useRef();
    // const { addPostLoading, addPostDone } = useSelector((state) => state.post);
    const { addPostLoading, addPostDone, imagePaths } = useSelector((state) => state.post);

    useEffect(() => {
        if (addPostDone) {
            setText('');
        }
    }, [addPostDone]);

    const onSubmit = useCallback((e) => {
        e.preventDefault();
        console.log("입력 text : " + text);

        // dispatch(addPost);
        // setText("");
        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                text,
            },
        });

    }, [text]);

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click();
    }, [imageInput.current]);

    const onChangeImages = useCallback((e) => {
        console.log('images : ', e.target.files);

        const imageFormData = new FormData();
        [].forEach.call(e.target.files, (f) => {
            imageFormData.append('image', f);
        });

        console.log("imageFormData : ", imageFormData);

        dispatch({
            type: UPLOAD_IMAGES_REQUEST,
            data: imageFormData,
        });

    });

    return (
        <TweetForm onSubmit={onSubmit}>
            <TweetTextArea
                rows={4}
                value={text}
                onChange={onChangeText}
                maxLength={140}
                placeholder="어떤 신기한 일이 있었나요?"
            />
            <input type="file" name="image" multiple hidden ref={imageInput} onChange={onChangeImages} />
            <Button onClick={onClickImageUpload}>이미지 업로드</Button>
            <Button type="submit" style={{ float: 'right' }} htmlType="submit" loading={addPostLoading}>Tweet</Button>
        </TweetForm>
    )
}


export default PostForm
