import React, { useCallback, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Avatar, Card } from 'antd';
import { END } from 'redux-saga';
import Head from 'next/head';
import { useRouter } from 'next/router';

import axios from 'axios';
import { LOAD_USER_POSTS_REQUEST } from '../../reducers/post';
import { LOAD_MY_INFO_REQUEST, LOAD_USER_REQUEST } from '../../reducers/user';
import PostCard from '../../components/PostCard';
import wrapper from '../../store/configureStore';
import AppLayout from '../../components/AppLayout';

const User = () => {
    const dispatch = useDispatch();
    const router = useRouter();
    const { id } = router.query;
    const { mainPosts, hasMorePosts, loadUserPostsLoading } = useSelector((state) => state.post);
    const { userInfo } = useSelector((state) => state.user);

    useEffect(() => {
        dispatch({
            type: LOAD_USER_REQUEST,
            data: id,
        });
        // dispatch({
        //     type: LOAD_USER_POSTS_REQUEST,
        //     data: id,
        // });
        // dispatch({
        //     type: LOAD_MY_INFO_REQUEST,
        // });

        console.log("userInfo: ", userInfo);

    }, [])

    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
                if (hasMorePosts && !loadUserPostsLoading) {
                    dispatch({
                        type: LOAD_USER_POSTS_REQUEST,
                        lastId: mainPosts[mainPosts.length - 1] && mainPosts[mainPosts.length - 1].id,
                        data: id,
                    });
                }
            }
        };
        window.addEventListener('scroll', onScroll);
        return () => {
            window.removeEventListener('scroll', onScroll);
        };
    }, [mainPosts.length, hasMorePosts, id]);

    return (
        <AppLayout>
            <div> {id} 로 유저 정보, 유저 포스팅 정보 검색 하기</div>

            {userInfo && userInfo.nickname} 님의 포스팅 목록은 다음과 같습니다.

            {mainPosts.map((c) => (
                <PostCard key={c.id} post={c} />
            ))}

        </AppLayout>

        // <AppLayout>
        //     <Head>
        //         <title>
        //             {userInfo.nickname}
        //   님의 글
        // </title>
        //         <meta name="description" content={`${userInfo.nickname}님의 게시글`} />
        //         <meta property="og:title" content={`${userInfo.nickname}님의 게시글`} />
        //         <meta property="og:description" content={`${userInfo.nickname}님의 게시글`} />
        //         <meta property="og:image" content="https://nodebird.com/favicon.ico" />
        //         <meta property="og:url" content={`https://nodebird.com/user/${id}`} />
        //     </Head>
        //     {userInfo
        //         ? (
        //             <Card
        //                 actions={[
        //                     <div key="twit">
        //                         짹짹
        //         <br />
        //                         {userInfo.Posts}
        //                     </div>,
        //                     <div key="following">
        //                         팔로잉
        //         <br />
        //                         {userInfo.Followings}
        //                     </div>,
        //                     <div key="follower">
        //                         팔로워
        //         <br />
        //                         {userInfo.Followers}
        //                     </div>,
        //                 ]}
        //             >
        //                 <Card.Meta
        //                 // avatar={<Avatar>{userInfo.nickname[0]}</Avatar>}
        //                 // title={userInfo.nickname}
        //                 />
        //             </Card>
        //         )
        //         : null}
        //     {mainPosts.map((c) => (
        //         <PostCard key={c.id} post={c} />
        //     ))}
        // </AppLayout>
    );
};

