import shortId from 'shortid';


const dummyPost = (data) => ({
    id: data.id,
    content: data.content,
    User: {
        id: 1,
        nickname: '제로초',
    },
    Images: [],
    Comments: [],
});

const dummyComment = (data) => ({
    id: shortId.generate(),
    content: data,
    User: {
        id: 1,
        nickname: "제로초",
    },
});


export const initialState = {
    mainPosts: [
        {
            id: 1,
            User: {
                id: 1,
                userId: 'terecal',
                nickname: '펭하',
            },
            content: 'sample 게시글 #test',
            Images: [{
                src: 'https://image.shutterstock.com/image-vector/sample-red-square-grunge-stamp-260nw-338250266.jpg',
            }, {
                src: 'https://gimg.gilbut.co.kr/book/BN001958/rn_view_BN001958.jpg',
            }, {
                src: 'https://gimg.gilbut.co.kr/book/BN001998/rn_view_BN001998.jpg',
            }],
            Comments: [{
                User: {
                    nickname: 'nero',
                },
                content: '첫번째 더미 댓글 fisrt ',
            }, {
                User: {
                    nickname: 'hero',
                },
                content: '두번째 더미 댓글 second',
            }]
        }
    ],

    addPostLoading: false,
    addPostDone: false,
    addPostError: null,

    addCommentLoading: false,
    addCommentDone: false,
    addCommentError: null,


};

export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';
// 댓글 추가
export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST_REQUEST:
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null,
            };
        case ADD_POST_SUCCESS:

            return {
                ...state,
                addPostLoading: false,
                addPostDone: true,
                mainPosts: [dummyPost(action.data), ...state.mainPosts],
            };
        case ADD_POST_FAILURE:
            return {
                ...state,
                addPostLoading: false,
                addPostError: action.error,
            };

        case ADD_COMMENT_REQUEST:
            return {
                ...state,
                addCommentLoading: true,
                addCommentDone: false,
                addCommentError: null,
            };


        // When:
        // 리덕스 사가에서 댓글 추가 성공시(db에 api 요청 성공시) 입력한 댓글 정보와 함께 요청

        // 어떤 정보가 넘어오나? 
        // 1.action.data.postId(본문 id), 
        // 2.action.data.content(추가한 댓글 정보), 
        // 3.action.data.userId(단 더미 댓글 정보에는 사용하지 않음 )

        // 리턴값 설정 하기:
        // 불변성 법칙(기존의 state 형식+ 댓글 추가 정보로 전체 갱신 객체를 재구성)을 준수 해야 함
        // ex) state{ 그외, mainPosts:[그외, {그외,Comments[{기존 댓글 정보 + 추가 댓글 정보}]}], 댓글 추가 관련 상태 변수 설정 }

        // how: 
        // 1.mainPosts 부분 갱신
        // mainPosts를 재구성할때 그외들은 포스팅 개수만큼 존재하므로 일일이 설정하는것은 비효율적이고 어려우므로 부분 갱신)
        // ex)
        // mainPosts[index] = {...mainPosts[postIndex], Comments} 이런식으로 부분 갱신 (index: 본문 id로 조회 , 본문 정보(...mainPosts[index]) 로 조회 )
        // 2.전체 리턴값 설정(...mainPosts 활용)
        // ex)
        // {...state, mainPosts, 댓글 추가 관련 상태 변수(loading, error, done)}

        case ADD_COMMENT_SUCCESS:
            // 부분 갱신할 mainPosts 배열 객체 생성
            const mainPosts = [...state.mainPosts]

            // 본문글 인덱스 조회 by action.data.postId
            postIndex = mainPosts.findIndex(
                (v) => v.id == action.data.postId
            );
            // 갱신용 댓글 정보 배열 생성 (본문 댓글 정보 + 추가 댓글 정보)
            Comments = [...mainPosts[postIndex].Comments, dummyComment(action.data.text)]

            // ...을 활용하여 mainPosts를 부분 갱신 
            mainPosts[postIndex] = { ...mainPosts[postIndex], Comments };

            return {
                ...state, // 포스팅 정보 이외의 state
                mainPosts, // 포스팅 정보
                addCommentLoading, // 댓글 추가 로딩 끝
                addCommentError: false, // 댓글 추가 관련 에러 없음
                addCommentDone: true, // 댓글 추가 완료
            }

        case ADD_COMMENT_FAILURE:
            return {
                ...state,
                addCommentLoading: false,
                addCommentError: action.error,
            };



        default:
            return state;
    }
};

export default reducer;
