    import React, { useCallback } from 'react'
    import { TweetForm, TweetButton, TweetTextArea } from "./style/PostFormStyle.js";
    import useInput from '../hooks/useInput';




    function PostForm() {
        const [text, onChangeText] = useInput("");

        const onSubmit = useCallback((e) => {
            e.preventDefault();
            console.log("입력 text : " + text);
        }, [text]);

        return (
            <TweetForm onSubmit={onSubmit}>
                <TweetTextArea
                    rows={4}
                    value={text}
                    onChange={onChangeText}
                    maxLength={140}
                    placeholder="어떤 신기한 일이 있었나요?"
                />
                <input type="file" multiple hidden />
                <TweetButton primary >이미지 업로드</TweetButton>
                <TweetButton type="submit" primary right>Tweet</TweetButton>
            </TweetForm>
        )
    }


    export default PostForm
