// 리듀서에는 기본적으로 세가지가 필요 하다.
// state 설정, 액션 함수, 스위치문 

// state 설정은 외부 state 설정이며 
// 사용하는 방법은 다음과 같다.
// import { useSelector } from "react-redux";
// const isLoggedIn = useSelector((state) => state.user.isLoggedIn)

// 액션 함수는 state값을 변경하기 위한 함수이며 
// 실행 방법은 다음과 같다.
// 임포트
// import { useDispatch } from "react-redux";
// 컴퍼넌트 내에서 객체 생성
// const dispatch = useDispatch();
// 실행
// dispatch(loginAction({ id, password }));


const initialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
    },
    post: {
        mainPosts: [],
    },
};

export const loginAction = (data) => {
    return {
        type: "LOG_IN",
        data,
    };
};

export const logoutAction = () => {
    return {
        type: "LOG_OUT",
    };
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "LOG_IN":
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    user: action.data,
                },
            };
        case "LOG_OUT":
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                },
            };
        default: {
            return {
                ...state,
            };
        }
    }
};

export default rootReducer;
