import AppLayout from "../components/AppLayout";
import Head from "next/head";
import NicknameEditForm from '../components/NicknameEditForm';
import FollowList from '../components/FollowList';
import Router from 'next/router';
import { useSelector, useDispatch, } from 'react-redux';
import { LOAD_FOLLOWERS_REQUEST, LOAD_FOLLOWINGS_REQUEST } from '../reducers/user';

// useState, useCallback
import React, { useEffect, useState, useCallback  } from "react";
// // fetcher 추가
// const fetcher = (url) => axios.get(url, { withCredentials: true }).then((result) => result.data);
// // swr 모듈 임포트 
// import useSWR from 'swr';
// wrapper 임포트
import wrapper from '../store/configureStore';
// axios
import { END } from 'redux-saga';
import axios from 'axios';

const Profile = () => {
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);

    if (!me) {
        return null;
    }

    useEffect(() => {
        if (!(me && me.id)) {
            Router.push('/');
        }
    }, [me && me.id]);

    // 4줄 추가
    // const [followingsLimit, setFollowingsLimit] = useState(3);
    // const [followersLimit, setFollowersLimit] = useState(3);
    // const { data: followingsData, error: followingError } = useSWR(`http://localhost:3065/user/followings?limit=${followingsLimit}`, fetcher);
    // const { data: followersData, error: followerError } = useSWR(`http://localhost:3065/user/followers?limit=${followersLimit}`, fetcher);

    // console.log("followingsData : ", followingsData);
    // console.log("followersData : ", followersData);

    // 함수 2개 추가
    const loadMoreFollowers = useCallback(() => {
        setFollowersLimit((prev) => prev + 3);
    }, []);

    const loadMoreFollowings = useCallback(() => {
        setFollowingsLimit((prev) => prev + 3);
    }, []);

    useEffect(() => {
        dispatch({
            type: LOAD_FOLLOWERS_REQUEST,
        });
        dispatch({
            type: LOAD_FOLLOWINGS_REQUEST,
        });
    }, []);

    return (
        <>
            <Head>
                <title>내 프로필 | NodeBird</title>
            </Head>

            <AppLayout>
                <NicknameEditForm />
                {/* <FollowList header="팔로잉 목록" data={me.Followings} />
                <FollowList header="팔로워 목록" data={me.Followers} /> */}
                <FollowList header="팔로잉" data={me.followings} onClickMore={loadMoreFollowings} loading={!followingError && !followingsData} />
                <FollowList header="팔로워" data={me.followers} onClickMore={loadMoreFollowers} loading={!followerError && !followersData} />

            </AppLayout>
        </>
    );
};


export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    console.log('getServerSideProps start');
    console.log(context.req.headers);
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
        axios.defaults.headers.Cookie = cookie;
    }
    context.store.dispatch({
        type: LOAD_USER_REQUEST,
    });
    context.store.dispatch(END);
    console.log('getServerSideProps end');
    await context.store.sagaTask.toPromise();
});

export default Profile;
