import React from "react";
import PropTypes from "prop-types";
import { StopOutlined } from "@ant-design/icons";

import { List, Card, Button, Row, Col } from "antd";
import { followCardWrapper } from "../components/style/followListStyle";




// const FollowList = ({ header, data }) => {
const FollowList = ({ header, data, onClickMore, loading }) => {

    return (
        <List
            style={{ marginBttom: 20 }}
            grid={{ gutter: 4, xs: 2, md: 3 }}
            size="small"
            header={<div>{header}</div>}
            // loadMore={
            //     <div style={{ textAlign: "center", margin: "10px 0" }}>
            //         <Button style={{ width: "100%" }}>더보기</Button>
            //     </div>
            // }
            loadMore={<div style={{ textAlign: 'center', margin: '10px 0' }}><Button onClick={onClickMore} loading={loading}>더 보기</Button></div>}

            bordered
            dataSource={data}
            renderItem={(item) => (
                <List.Item style={{ marginTop: "20px" }} >
                    <Col key={item.id} span={48}>
                        <Card actions={[<StopOutlined key="stop" />]}>
                            <Card.Meta description={item.nickname} />
                        </Card>
                    </Col>
                </List.Item>
            )}
        />
    );
};

FollowList.propTypes = {
    header: PropTypes.string.isRequired,
    data: PropTypes.array.isRequired,
};

export default FollowList;
