import React, { useCallback } from 'react';
import { Button } from 'antd'
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import { FOLLOW_REQUEST, UNFOLLOW_REQUEST } from '../reducers/user';


const FollowButton = ({ post }) => {
    const dispatch = useDispatch();
    const { me, followLoading, unfollowLoading, followError, unfollowError } = useSelector((state) => state.user);

    const isFollowing = me?.Followings.find((v) => v.id === post.User.id);

    if (followError) {
        alert("followError : ", followError)
    }
    if (unfollowError) {
        alert("unfollowError : ", unfollowError)
    }

    const onClickButton = useCallback(() => {
        console.log("isFollowing(FollowButton) : " , isFollowing);
        if (isFollowing) {
            dispatch({
                type: UNFOLLOW_REQUEST,
                data: post.User.id,
            });
        } else {
            dispatch({
                type: FOLLOW_REQUEST,
                data: post.User.id,
            });
        }
    }, [isFollowing]);

    return (
        <Button loading={followLoading || unfollowLoading} onClick={onClickButton}>
            {isFollowing ? '언팔로우' : '팔로우'}
        </Button>
    )
};


FollowButton.propTypes = {
    post: PropTypes.object.isRequired,
}

export default FollowButton;
