import React from 'react'
import { Form, Input } from 'antd';
import styled from "styled-components";

const { TextArea } = Input;


const TweetButton = styled.button`
  background: ${props => props.blueColor ? "blue" : "white"};
  color: ${props => props.blueColor ? "white" : "palevioletred"};
  font-size: 1em;
  margin: 5px;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
  display: flex; 
  justify-content: space-between;
  float: ${props => props.rightSide ? "right" : "left" };
`;






function PostForm() {
    return (
        <>
            <TextArea rows={4} />
            <TweetButton>이미지 업로드</TweetButton>
            <TweetButton rightSide blueColor>Tweet</TweetButton>
        </>
    )
}

export default PostForm
