import React, { useState, useCallback } from "react";
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';
import CommentForm from './CommentForm';
import PostImages from "./PostImages";

import { Card, Avatar, List, Comment, Popover, Button } from 'antd';
import PostCardContent from "../components/PostCardContent";

import { useSelector, useDispatch } from 'react-redux';
import { REMOVE_POST_REQUEST } from '../reducers/post';
import FollowButton from './FollowButton';



const PostCard = ({ post }) => {

    console.log("postCard 로 넘어온 이미지 정보 확인 :", post.Images[0]);

    const dispatch = useDispatch();
    const { removePostLoading } = useSelector((state) => state.post);

    const [commentFormOpened, setCommentFormOpened] = useState(false);

    const { me } = useSelector((state) => state.user);
    const id = me && me.id;

    const onToggleComment = useCallback(() => {
        setCommentFormOpened((prev) => !prev);
    }, []);

    const onRemovePost = useCallback(() => {
        dispatch({
            type: REMOVE_POST_REQUEST,
            data: post.id,
        });
    }, []);

    return (
        <>
            <Card
                style={{ width: "100%" }}
                // cover={post.Images[0] && <img alt="example" src={post.Images[0].src} />}
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key="retweet" />,
                    <HeartOutlined key="heart" onClick="" />,
                    <MessageOutlined key="message" onClick={onToggleComment} />,

                    <Popover
                        key="ellipsis"
                        content={(
                            <Button.Group>
                                {id && post.User.id === id
                                    ? (
                                        <>
                                            <Button>수정</Button>
                                            <Button type="danger" loading={removePostLoading} onClick={onRemovePost}>삭제</Button>
                                        </>
                                    )
                                    : <Button>신고</Button>}
                            </Button.Group>
                        )}
                    >
                        <EllipsisOutlined />
                    </Popover>,
                ]}
                extra={< FollowButton post={post} />}
            >
                <Card.Meta
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    description={<PostCardContent postData={post.content} />}
                />
            </Card>,

            {commentFormOpened && (
                <>
                    <CommentForm post={post} />
                    <List
                        header={`${post.Comments ? post.Comments.length : 0} 댓글`}
                        itemLayout="horizontal"
                        dataSource={post.Comments || []}
                        renderItem={(item) => (
                            <li>
                                <Comment
                                    author={item.User.nickname}
                                    avatar={<Avatar>{item.User.nickname[0]}</Avatar>}
                                    content={item.content}
                                />
                            </li>
                        )}
                    />
                </>
            )}


        </>
    );
};

export default PostCard;
